mapper.insert:
INSERT INTO tbl_envios (
    numeroGuia, idCliente, destinatarioNombre, destinatarioAp1, destinatarioAp2,
    codigoSucursalOrigenFk, destinoCalle, destinoNumero, idColoniaDestino,
    costoTotal, idEstatus
) VALUES (
    #{numeroGuia}, #{idCliente}, #{destinatarioNombre}, #{destinatarioAp1}, #{destinatarioAp2},
    #{codigoSucursalOrigen}, #{destinoCalle}, #{destinoNumero}, #{idColoniaDestino},
    #{costoTotal}, #{idEstatus}
)

INSERT INTO tbl_historial_estatus (
    idEnvio, numeroPersonalColaborador, idEstatus, comentario, fechaHora
) VALUES (
    #{idEnvio}, #{numeroPersonalColaborador}, #{idEstatus}, #{comentario}, NOW()
)

mapper.select:
SELECT codigo_postal 
FROM colonias 
WHERE id = #{idColonia}

SELECT idEnvio, codigoSucursalOrigenFk AS codigoSucursalOrigen, idColoniaDestino 
FROM tbl_envios 
WHERE idEnvio = #{idEnvio}
